mapper.select:
select
  id, main_id, product_inv_code, product_inv_name, product_inv_std, product_inv_unit, product_qty, material_price, material_amount, work_price, total_work_amount, price, amount, plan_start_date, plan_end_date, create_user, create_date, update_user, update_date, iz_delete, delete_user, delete_date, u8_mo_detail_id, row_no, tax_rate, record_id
from om_order_detail


        and id = #{id}


        and main_id = #{mainId}


        and product_inv_code = #{productInvCode}


        and product_inv_name = #{productInvName}


        and product_inv_std = #{productInvStd}


        and product_inv_unit = #{productInvUnit}


        and product_qty = #{productQty}


        and material_price = #{materialPrice}


        and material_amount = #{materialAmount}


        and work_price = #{workPrice}


        and total_work_amount = #{totalWorkAmount}


        and price = #{price}


        and amount = #{amount}


        and plan_start_date = #{planStartDate}


        and plan_end_date = #{planEndDate}


        and create_user = #{createUser}


        and create_date = #{createDate}


        and update_user = #{updateUser}


        and update_date = #{updateDate}


        and iz_delete = #{izDelete}


        and delete_user = #{deleteUser}


        and delete_date = #{deleteDate}


        and u8_mo_detail_id = #{u8MoDetailId}


        and row_no = #{rowNo}


        and tax_rate = #{taxRate}


        and record_id = #{recordId}

select count(1)
from om_order_detail


        and id = #{id}


        and main_id = #{mainId}


        and product_inv_code = #{productInvCode}


        and product_inv_name = #{productInvName}


        and product_inv_std = #{productInvStd}


        and product_inv_unit = #{productInvUnit}


        and product_qty = #{productQty}


        and material_price = #{materialPrice}


        and material_amount = #{materialAmount}


        and work_price = #{workPrice}


        and total_work_amount = #{totalWorkAmount}


        and price = #{price}


        and amount = #{amount}


        and plan_start_date = #{planStartDate}


        and plan_end_date = #{planEndDate}


        and create_user = #{createUser}


        and create_date = #{createDate}


        and update_user = #{updateUser}


        and update_date = #{updateDate}


        and iz_delete = #{izDelete}


        and delete_user = #{deleteUser}


        and delete_date = #{deleteDate}


        and u8_mo_detail_id = #{u8MoDetailId}


        and row_no = #{rowNo}


        and tax_rate = #{taxRate}


        and record_id = #{recordId}
